use super::*;
use std::io::Write;

impl CppGenerator {
    pub fn header<T: Write>(w: &mut PrettyWriter<T>) -> Result<(), Error> {
        w.write_lines(
            "
/*
 * This file was automatically generated by witx-codegen - Do not edit manually.
 */
#include <cstdint>
#include <cstring>
#include <string_view>
#include <tuple>
#include <cstddef>

// namespace WitxCodegenHeader {
using WasiHandle = int32_t;
template <typename T> using WasiPtr = T *const;
template <typename T> using WasiMutPtr = T *;
using WasiStringBytesPtr = WasiPtr<unsigned char>;

struct WasiString {
    WasiStringBytesPtr ptr;
    size_t length;
};

template<typename T>
struct WasiSlice {
    WasiPtr<T> ptr;
    size_t length;
};

template<typename T>
struct WasiMutSlice {
    WasiMutPtr<T> ptr;
    size_t length;
};

// }
",
        )?
        .eob()?;
        Ok(())
    }
}
